
public class Compare {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int n1 = 5;
		int n2 = 7;
		if(n1 < n2) {
			System.out.printf("n1:%d, n2:%d \n ------ \n", n1, n2);
			System.out.println("n1 < n2 is ture");
		}
		
		if(n1 == n2) {
			System.out.println("n1 == n2 is ture");
		}
		else {
			System.out.println("n1 == n2 is false");
		}
	}

}
