
public class ArithOp {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int  num1 = 7;
		int num2 = 3;
		//연산자 순위 가장 높은것 --> () //괄호
		System.out.println("num 1 + num2 = " + (num1 + num2));
		System.out.println("num 1 - num2 = " + (num1 - num2));
		System.out.println("num 1 * num2 = " + (num1 * num2));
		System.out.println("num 1 / num2 = " + (num1 / num2));
		System.out.println("num 1 % num2 = " + (num1 % num2));
	}

}
